  `tec_description` varchar(255) NOT NULL,
  `tec_phone` varchar(255) NOT NULL,
  PRIMARY KEY (`tec_id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

